from sqlalchemy import Column, Integer, String, ForeignKey, Tablefrom sqlalchemy.orm import relationshipfrom database import BaseSubjectTopic = Table("subject_topic", Base.metadata,                     Column('id', Integer, primary_key=True),                     Column('subject_id', Integer, ForeignKey('subjects.id')),                     Column('topic_id', Integer, ForeignKey('topics.id')),                     )class Subjects(Base):    __tablename__ = "subjects"    id = Column(Integer, primary_key=True, index=True)    name = Column(String, )    topics = relationship("Topics", secondary=SubjectTopic, backref="subjects", lazy="joined")class Topics(Base):    __tablename__ = "topics"    id = Column(Integer, primary_key=True, index=True)    question = Column(String)    answer = Column(String)